import fetch from 'isomorphic-unfetch'

const Information = (props) => (
    <div>
       <h1>{props.country.name}</h1>
    </div>
)

Information.getInitialProps = async(url) => {
  const id  = url.query.id
  console.log(id);
  let uri = `https://restcountries.eu/rest/v2/name/${id}?fullText=true`
  console.log(uri)
  const res = await fetch(`https://restcountries.eu/rest/v2/name/${id}?fullText=true`)
  const country = await res.text()

  console.log(`Fetched country: ${country.alpha3Code}`)

  return { country }
}

export default Information
