import Layout from ".././Components/Layout.js";
import Header from ".././Components/Header.js"
import fetch from 'isomorphic-unfetch'

const Information = (props) => (
    <Layout>
       <Header name={props.country.name} link="/" title="Home" />
       <div className="info">
         <h4>Region: {props.country.region}</h4>
         <h4>Subregion: {props.country.subregion}</h4>
         <h4>Capital: {props.country.capital}</h4>
         <h4>Population: {props.country.population}</h4>
         <h4>Currency: {props.country.currencies[0].name}</h4>
       </div>
       <style jsx>{`
      .info {
        margin-left: 1%;
      }
    `}</style>
       </Layout>
)

Information.getInitialProps = async(url) => {
  const id  = url.query.id
  console.log(id);
  const res = await fetch(`https://restcountries.eu/rest/v2/name/${id}?fullText=true`)
  const country = await res.text()

  console.log(`Fetched country: ${country.name}`)

  return { country }
}

export default Information
